// ============================================================================
// HELPER FUNCTIONS
// ============================================================================

export function getDocumentsByCategory(category: DocumentCategory): DocumentTemplate[] {
  return EXTENDED_DOCUMENT_TEMPLATES.filter(doc => doc.category === category);
}

export function getDocumentById(id: ExtendedDocumentType): DocumentTemplate | undefined {
  return EXTENDED_DOCUMENT_TEMPLATES.find(doc => doc.id === id);
}

export function getAllCategories(): DocumentCategory[] {
  return [...new Set(EXTENDED_DOCUMENT_TEMPLATES.map(doc => doc.category))];
}

export function getDocumentCount(): number {
  return EXTENDED_DOCUMENT_TEMPLATES.length;
}

