// ============================================================================
// DOCUMENT TEMPLATE INTERFACE
// ============================================================================

export interface DocumentTemplate {
  id: ExtendedDocumentType;
  title: string;
  description: string;
  category: DocumentCategory;
  defaultPageLength: PageLength;
  minPageLength: PageLength;
  maxPageLength: PageLength;
  timeToGenerate: string;
  requiredInputs: string[];
  optionalInputs: string[];
  outputFormats: ('pdf' | 'docx' | 'html' | 'md' | 'txt')[];
  useCases: string[];
  targetAudience: string[];
  sections: string[];
  aiCapabilities: string[];
}

