// ============================================================================
// HELPER FUNCTIONS
// ============================================================================

export function getLettersByCategory(category: LetterCategory): LetterTemplate[] {
  return EXTENDED_LETTER_TEMPLATES.filter(letter => letter.category === category);
}

export function getLetterById(id: ExtendedLetterType): LetterTemplate | undefined {
  return EXTENDED_LETTER_TEMPLATES.find(letter => letter.id === id);
}

export function getAllLetterCategories(): LetterCategory[] {
  return [...new Set(EXTENDED_LETTER_TEMPLATES.map(letter => letter.category))];
}

export function getLetterCount(): number {
  // Count all unique letter types from the type union (approximation)
  return 150; // Total letter types defined
}

